      },
      itemStyle: {
        areaColor: 'yellow'
      }
    }
  }],
  tooltip: {
    trigger: 'item',
    formatter: '地区：{b}<br/>新增本土确诊：{c}'
  },
  visualMap: {
    type: 'piecewise',
    pieces: [{
      min: 10000
    }, {
      min: 1000,
      max: 9999
    }, {
      min: 100,
      max: 999
    }, {
      min: 10,
      max: 99
    }, {
      min: 5,
      max: 9
    }, {
      min: 1,
      max: 4
    }, {
      value: 0
    }],
    inRange: {
      color: ['#fff', "#ffaa85", "#660208"]
    },
    itemWidth: 20,
    itemHeight: 15
  }
};
export default {
  name: 'MapYesterDay',

  data() {
    return {};
  },

  mounted() {
    axios.post('/api').then(res => {
      console.log(res);
      const keyArr = Object.keys(res.data.data.provinceCompare);
      const co = [];

      for (var i = 0; i < keyArr.length; i++) {
        co[i] = {
          name: keyArr[i],
          value: res.data.data.provinceCompare[keyArr[i]]
        };
      }

      const lists = co.map(item => {
        return {
          name: item.name,
          value: item.value.confirmAdd
        };
      });
      option.series[0].data = lists;
      echart.init(this.$refs.echartf).setOption(option);
    });
  }

};
