import * as echart from "echarts";
import axios from 'axios';
export default {
  data() {
    return {};
  },

  mounted() {
    axios.post('/api').then(res => {
      console.log(res);
      const list = res.data.data.provinceCompare;
      const keyArr = Object.keys(list);
      const series = [];
      keyArr.forEach(key => {
        series.push({
          name: key,
          data: list[key].confirmAdd,
          type: 'bar'
        });
      });
      console.log("series", series);
      const option = {
        xAxis: {
          type: "category",
          data: keyArr,
          axisLine: {
            lineStyle: {
              color: "#17b3a3"
            }
          },
          axisLable: {
            interval: 0,
            color: "#333"
          }
        },
        yAxis: {},
        series
      };
      echart.init(this.$refs.echartf).setOption(option);
    });
  }

};
