import * as echart from "echarts";
import axios from 'axios';
export default {
  data() {
    return {};
  },

  mounted() {
    axios.post('/api').then(res => {
      console.log(res);
      const list = res.data.data.provinceCompare;
      const keyArr = Object.keys(list);
      const series = [];
      keyArr.forEach(key => {
        series.push({
          name: key,
          data: list[key].confirmAdd,
          type: 'bar'
        });
      });
      const option = {
        title: {
          text: '昨日新增新冠肺炎本土确诊病例'
        },
        xAxis: {
          type: "category",
          data: keyArr,
          axisLine: {
            lineStyle: {
              color: "#17b3a3"
            }
          },
          axisLable: {
            interval: 0,
            color: "#333"
          }
        },
        yAxis: {},
        series
      };
      const E = echart.init(this.$refs.echart);
      E.setOption(option);
    });
  }

};
